import React, { useState } from 'react';
import { Chart } from 'react-apexcharts';
import { HiTrendingUp } from 'react-icons/hi';
import styles from './AreaCard.module.css';

export default function AreaCard({ work, number, rise }) {
  let profit = false;
  if (rise === 'up') {
    profit = true;
  } else {
    profit = false;
  }

  const [state, setState] = useState({
    series: [
      {
        name: 'STOCK ABC',
        data: [],
      },
    ],
    options: {
      chart: {
        type: 'area',
        height: 100,
        zoom: {
          enabled: false,
        },
      },
      dataLabels: {
        enabled: false,
      },
      stroke: {
        curve: 'straight',
      },
      title: {
        text: 'Fundamental Analysis of Stocks',
        align: 'left',
      },
      subtitle: {
        text: 'Price Movements',
        align: 'left',
      },
      labels: ['M', 'T', 'W', 'Th', 'F', 'S'],
      xaxis: {
        type: 'datetime',
      },
      yaxis: {
        opposite: true,
      },
      legend: {
        horizontalAlign: 'left',
      },
      colors: ['#1f994e'],
    },
  });

  return (
    <>
      <div className={styles.chart} id="chart">
        <p>{work}</p>
        <div style={{ display: 'flex' }}>
          <div>
            <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
              <h5>{number}</h5>
              {profit ? (
                <div style={{ height: '15px', width: '15px', background: '#98d490' }}>
                  <HiTrendingUp color="#055e0e" />
                </div>
              ) : (
                <div style={{ height: '15px', width: '15px', background: '#e8898f' }}>
                  <HiTrendingUp color="#7d0c14" />
                </div>
              )}
            </div>
          </div>
          <Chart options={state.options} series={state.series} type="area" height={100} />
        </div>
      </div>
    </>
  );
}
